package OSaft::Doc::Data;

use strict;
use warnings;

BEGIN { # mainly required for testing ...
    # SEE Perl:BEGIN perlcritic
    my $_path = $0;     $_path =~ s#[/\\][^/\\]*$##;
    unshift(@INC, ".",  $_path);
}

