    sub is_valid_key {
        my $key = uc(shift);
        $key =~ s/^0X//g;
        return "" if $key !~ m/^[0-9A-F]{8}$/;
        return "0x$key";
    }

    sub text2key {
        my $txt = shift;
        my $key = uc($txt);
        $key =~ s/(,|0X)//g;
        return $txt     if ( $key !~ m/^[0-9A-F]+$/ );
        return "0x$key" if ( 8 == length($key) );
        if ( 4 < length($key) ) {
            $key = "0x02$key";
        }
        else {
            while ( 6 > length($key) ) { $key = "0$key"; }
            $key = "0x03$key";
        }
        return $key;
    }

    sub key2text {
        my $key = shift;
        return $key if not is_valid_key($key);

        $key =~ s/0x//i;
        if ( 6 < length($key) ) {
            $key =~ s/^42//;
            $key =~ s/^02//;
            $key =~ s/^0300//;
        }
        $key =~ s/(..)/,0x$1/g;
        $key =~ s/^,//;
        $key = "     $key" if ( 10 > length($key) );
        return "$key";
    }

