    sub _man_pod_text {
        my $code  = 0;
        my $empty = 0;
        my $pod;
        while ( $_ = shift @help ) {
            last if m/^(?:=head[1] )?END\s+#/;
            m/^$/ && do {
                if ( 0 == $empty ) { $pod .= $_; $empty++; }
                next;
            };
            s/^(\s*(?:o-saft\.|checkAll|yeast\.).*)/S&$1&/;
            s/^ {1,13}//;
            s/^S&\s*([^&]*)&/\t$1/ && do {
                $pod .= "\n" if ( 0 == ( $empty + $code ) );
                $pod .= $_;
                $empty = 0;
                $code++;
                next;
            };
            $code = 0;
            s:['`]([^']*)':C<$1>:g;
            s:(^|\s)X&([^&]*)&:$1L</$2>:g;
            s:(^|\s)L&([^&]*)&:$1L<$2|$2>:g;

            s:(^|\s)I&([^&]*)&:$1I<$2>:g;
            s/^([A-Z., -]+)$/B<$1>/;
            s/^(=item)\s+(.*)/$1 $2/;
            my $line = $_;
            m/^=/ && do {

                $pod .= "\n"                  if ( 0 == $empty );
                $pod .= "$line"               if $line =~ m/^=[hovbefpc].*/;
                $pod .= _man_pod_item "$line" if $line =~ m/^=item/;
                $pod .= "\n";
                $empty = 1;
                next;
            };
            $pod .= "$line";
            $empty = 0;
        }
        return $pod;
    }

