            _p_k_v( "::trace",         $SSLinfo::trace );
            _p_k_v( "::linux_debug",   $SSLinfo::linux_debug );
            _p_k_v( "::slowly",        $SSLinfo::slowly );
            _p_k_v( "::timeout",       $SSLinfo::timeout );
            _p_k_v( "::use_openssl",   $SSLinfo::use_openssl );
            _p_k_v( "::use_sclient",   $SSLinfo::use_sclient );
