    return if ( 0 < _is_argv('(?:--no.?header|--cgi)') );
    return if ( 0 >= _is_argv('(?:--v$|--trace|--warn)') );
    if ( 0 >= _is_argv('(?:--trace[_.-]?(?:ARG|CMD|TIME|ME)$)') ) {
        return if ( 0 < _is_argv('(?:--trace[_.-]?CLI|KEY$)') );
